delay = 0.065

wn =  turtle.Screen()

# set up the screen
wn.tracer(0)
wn.title("Snake Game")
wn.bgcolor("#009933")

# color of the snake: #0066ff
# outline color of the snake: black
# background color : #009933

ek = turtle.Turtle()
ek.speed(0)

# snake head
def draw_snake():
    ek.penup()
    ek.color("white", "#0066ff")
    ek.shape("square")

draw_snake()

food = turtle.Turtle()
food.speed(0)

# spawn apples in random areas on screen
def draw_apple():
    food.color("black" , "red")
    food.shape("square")

# max 39 blocks fit horizontally
# max 29 fit vertically

minX = -385
maxX = 374
minY = -284
maxY = 275

def spawn_apples():
    import random
    ran = random.randrange(0 , len(all_possible_apple_positions))
    delete_positions.append((all_possible_apple_positions[ran]))
    if len(all_possible_apple_positions) > 0:
        food.penup()
        food.goto(all_possible_apple_positions[ran])
        print("delete cooridinate :" , all_possible_apple_positions[ran])
        food.pendown()
    else:
        print("Congrats you beat my game thanks for playing")
    draw_apple()
spawn_apples()

ek.direction = 'stop'

sc = turtle.Turtle()
sc.hideturtle()
sc.getscreen().bgcolor("#009933")

# game border field
sc.width(10)
sc.penup()
sc.goto((-400, -300))
sc.pendown()
sc.goto((390, -300))
sc.pendown()
sc.goto((390, 290))
sc.pendown()
sc.goto((-400, 290))
sc.pendown()
sc.goto((-400, -300))
sc.penup()
sc.pendown()

Scores = turtle.Turtle()
Scores.hideturtle()

updateScore = turtle.Turtle()
updateHighScore = turtle.Turtle()
updateScore.hideturtle()
updateHighScore.hideturtle()

#scores
score = 0
high_score = 0

def pause_time():
    pass

def reset_score():
    #reset the scoresboards
    updateScore.clear()
    Scores.clear()

#updateing the scoresboards
def update_score(value):
    style = ('Courier New', 30, 'bold')
    updateScore.clear()
    updateScore.color("blue")
    updateScore.penup()
    updateScore.goto(-150, 300)
    updateScore.pendown()
    updateScore.write(value, font=style, align='center')

def update_highscore(value):
    style = ('Courier New', 30, 'bold')
    updateHighScore.clear()
    updateHighScore.color("blue")
    updateHighScore.penup()
    updateHighScore.goto(310, 300)
    updateHighScore.pendown()
    updateHighScore.write(value, font=style, align='center')

# display score
sc.color("red")
style = ('Courier New', 30, 'bold')
sc.penup()
sc.goto(-300, 300)
sc.pendown()
sc.write('Score:', font=style, align='center')

# display high score
sc.penup()
sc.goto(100, 300)
sc.pendown()
sc.color("red")
style = ('Courier New', 30, 'bold')
sc.write('High score:', font=style, align='center')

# set the scores
reset_score()

#-------------------------------------------
# snake's movement
def go_up():
    if ek.direction != "down":
        ek.direction = "up"

def go_down():
    if ek.direction != "up":
        ek.direction = "down"

def go_left():
    if ek.direction != "right":
        ek.direction  = "left"

def go_right():
    if ek.direction != "left":
        ek.direction = "right"

def move():
    if ek.direction == "up":
        y = ek.ycor()
        ek.sety(y+20)
    if ek.direction == "down":
        y = ek.ycor()
        ek.sety(y-20)
    if ek.direction == "left":
        x = ek.xcor()
        ek.setx(x-20)
    if ek.direction == "right":
        x = ek.xcor()
        ek.setx(x+20)

# keyboard controls
wn.listen()
wn.onkeypress(go_up, "w")
wn.onkeypress(go_down, "s")
wn.onkeypress(go_left, "a")
wn.onkeypress(go_right, "d")

wn.onkeypress(go_up, "Up")
wn.onkeypress(go_down, "Down")
wn.onkeypress(go_left, "Left")
wn.onkeypress(go_right, "Right")

def snake_dies():
    wn.tracer(1)
    ek.color("white" , "grey")
    time.sleep(1)
    ek.color("white" , "#0066ff")
    ek.goto(0 , 0)

segments = []
last_score = 0
while True:
    wn.update()

    # check collsion with border area
    if ( ek.xcor() > maxX or ek.xcor() < minX  ) or ( ek.ycor() > maxY or ek.ycor() < minY ):
        delete_positions.clear()
        print("\nsnake died because it ran into the wall\n")
        print("\nclearing all deleted cooridinates\n")
        last_score = score
        snake_dies()
        ek.direction = "stop"

        for segment in segments:
            segment.penup()
            segment.goto(1000 , 1000)
        segments.clear()

        # reset the score value
        score = 0
        reset_score()

    # detect collision with food
    if food.distance(ek) < 20:
        spawn_apples()
        score += 10
        # updateing the scores
        update_score(score)

        # add a new segment to the snake's body
        new_segment = turtle.Turtle()
        new_segment.speed(0)
        new_segment.color("black" , "#0066ff")
        new_segment.penup()
        new_segment.shape("square")
        segments.append(new_segment)

        if score >= last_score:
            high_score = score
            update_highscore(high_score)

    # move the segments in reverse order
    for index in range(len(segments)-1, 0, -1):
        x = segments[index-1].xcor()
        y = segments[index-1].ycor()
        segments[index].goto(x, y)

    # move segment 0 to head
    if len(segments) > 0:
        x = ek.xcor()
        y = ek.ycor()
        segments[0].goto(x, y)

    move()

    # check for collsion with body
    for segment in segments:
        if segment.distance(ek) < 20:
            delete_positions.clear()
            print("\nsnake died because it ran into it's self\n")
            print("\nclearing all deleted cooridinates\n")
            score = 0
            reset_score()
            snake_dies()
            ek.direction = "stop"

            # hide segments
            for segment in segments:
                segment.penup()
                segment.goto(1000 , 1000)
            segments.clear()
            delay = 0.065

    time.sleep(delay)


turtle.done()

